import useRentModal from "../Hooks/useRentModal";
import Modal from "./Modal";

const RentModal = () => {

  const rentModal = useRentModal();

  return ( 
    <Modal
      isOpen={RentModal.isOpen}
      title="Airbnb your home"
    />
   );
}
 
export default RentModal;
